changeSet.sql:
create table application_user ( id bigint primary key, username text not null unique, name text not null, password text not null, enabled boolean not null default true, country text not null, zone_id text not null,
version integer, created_at timestamp with time zone, created_by text, updated_at timestamp with time zone, updated_by text );
create table application_user_role ( id bigint primary key, application_user_id bigint not null references application_user (id), role text not null,
version integer, created_at timestamp with time zone, created_by text, updated_at timestamp with time zone, updated_by text ); create index application_user_role_application_user_id_index on application_user_role (application_user_id);

insert into application_user_role (id, application_user_id, role, created_at, created_by)
values (1, (select id from application_user where username = 'admin'), 'ADMIN', now(), 'system');

insert into application_user_role (id, application_user_id, role, created_at, created_by)
values (2, (select id from application_user where username = 'admin'), 'USER', now(), 'system');

create table transcription ( id bigint primary key, status text not null, source_uri text not null, uri text, storage_provider text, language text not null, name text not null, application_user_id bigint references application_user (id) not null, length interval, error text,
version integer, created_at timestamp with time zone, created_by text, updated_at timestamp with time zone, updated_by text );
create index transcription_application_user_id_index on transcription (application_user_id);
