changeSet.sql:
create table application_user ( id bigserial primary key, username varchar(255) not null unique, name varchar(255) not null, password varchar(255) not null, enabled boolean not null default true,
created_at timestamp, created_by varchar(255), updated_at timestamp, updated_by varchar(255) );
create table application_user_role ( id bigserial primary key, application_user_id bigint not null references application_user (id), role varchar(255) not null,
created_at timestamp, created_by varchar(255), updated_at timestamp, updated_by varchar(255) );

insert into application_user_role (application_user_id, role, created_at, created_by)
values ((select id from application_user where username = 'admin'), 'ADMIN', now(), 'system');

insert into application_user_role (application_user_id, role, created_at, created_by)
values ((select id from application_user where username = 'admin'), 'USER', now(), 'system');

create table transcript_part ( id bigserial primary key, transcription_id bigint references transcription (id) not null, start_offset_millis bigint not null, end_offset_millis bigint not null, sequence integer not null,
created_at timestamp, created_by varchar(255), updated_at timestamp, updated_by varchar(255),
unique (transcription_id, sequence) ); create index transcript_part_transcription_id_index on transcript_part (transcription_id); create index transcript_part_sequence_index on transcript_part (sequence);

create table transcript_part_text ( id bigserial primary key, transcript_part_id bigint references transcript_part (id) not null, content text not null, version integer not null, type varchar(255) not null,
created_at timestamp, created_by varchar(255), updated_at timestamp, updated_by varchar(255),
unique (transcript_part_id, version) ); create index transcript_part_text_transcript_part_id_version_index on transcript_part_text (transcript_part_id, version); create index transcript_part_text_transcript_part_id_type_index on transcript_part_text (transcript_part_id, type);
